import React, { useState, useEffect, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Loading from "./Loading";
import styled from "styled-components";
import backgroundImage from "../asset/4.png";

const Admin = () => {
  const [loading, setLoading] = useState(true);
  const [applicationData, setApplicationData] = useState([]);
  const [pendingRequests, setPendingRequests] = useState([]);
  const navigate = useNavigate();

  const handleLogout = () => {
    navigate("/");
  };

  const fetchApplicationData = useCallback(async () => {
    try {
      const response = await fetch(
        "http://localhost:3001/api/get-application-data"
      );
      const data = await response.json();

      if (data.success) {
        setApplicationData(data.applicationData);
      } else {
        notifyError("Error fetching application data");
      }
    } catch (error) {
      console.error("Error fetching application data:", error);
    }
  }, []);

  const fetchPendingRequests = useCallback(async () => {
    try {
      const response = await fetch(
        "http://localhost:3001/api/pending-requests"
      );
      const data = await response.json();

      if (data.success) {
        setPendingRequests(data.requests);
      } else {
        notifyError("Error fetching application data");
      }
    } catch (error) {
      console.error("Error fetching pending requests:", error);
    }
  }, []);

  const handleApproveapp = async (id, type, applicationData) => {
    try {
      const response = await fetch(
        "http://localhost:3001/api/insert-student-details",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ id }),
        }
      );

      const data = await response.json();

      if (data.success) {
        notifyError("Student details inserted successfully.");
      } else {
        notifyError(data.message.error.detail);
      }
    } catch (error) {
      console.error("Error approving student:", error);
    }
  };

  const handleDenyapp = async (id) => {
    try {
      const response = await fetch(
        `http://localhost:3001/api/deny-requestapp/${id}`,
        {
          method: "DELETE",
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      const data = await response.json();
      if (data.success) {
        notifyError("Denied Successfully");
        fetchPendingRequests();
      } else {
        notifyError(data.message.error.detail);
      }
    } catch (error) {
      notifyError("Error denying request");
    }
  };

  const handleApprove = async (id, type, applicationData, department_name) => {
    try {
      const response = await fetch(
        "http://localhost:3001/api/approve-request/",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ id, applicationData, department_name }),
        }
      );

      const data = await response.json();

      if (data.success) {
        notifyError(data.message);
      } else {
        notifyError(data.message.error.detail);
      }
    } catch (error) {
      console.error("Error approving student:", error);
    }
  };

  const handleDeny = async (id, applicationData, department_name) => {
    try {
      const response = await fetch(`http://localhost:3001/api/approve-deny`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ id, applicationData, department_name }),
      });
      const data = await response.json();
      if (data.success) {
        notifyError("Denied Successfully");
        fetchPendingRequests();
      } else {
        notifyError(data.message.error.detail);
      }
    } catch (error) {
      console.error("Error denying request:", error);
    }
  };

  const notifyError = (message) => {
    toast.error(message, {
      position: "top-right",
      autoClose: 5000,
      hideProgressBar: false,
      closeOnClick: true,
      pauseOnHover: true,
      draggable: true,
    });
  };

  const groupRequestsByRollNumber = useCallback(() => {
    const groupedRequests = {};
    pendingRequests.forEach((request) => {
      const { roll_number } = request;
      if (!groupedRequests[roll_number]) {
        groupedRequests[roll_number] = [];
      }
      const existingRequest = groupedRequests[roll_number].find(
        (existing) => existing.department_name === request.department_name
      );
      if (!existingRequest) {
        groupedRequests[roll_number].push(request);
      }
    });
    return groupedRequests;
  }, [pendingRequests]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        await new Promise((resolve) => setTimeout(resolve, 2000));
        await Promise.all([fetchApplicationData(), fetchPendingRequests()]);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, [fetchApplicationData, fetchPendingRequests]);

  return (
    <>
      {loading ? (
        <Loading />
      ) : (
        <>
          <ToastContainer />
          <button
            className="logoutButton"
            style={{
              background: "#3498db",
              color: "#fff",
              padding: "10px 20px",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
              transition: "background-color 0.3s ease",
              marginTop: "10px",
              marginRight: "rem;",
            }}
            onClick={handleLogout}
          >
            Logout
          </button>
          <h2 style={{ color: "#3498db", marginTop: "20px" }}>Admin Page</h2>
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              marginTop: "20px",
            }}
          >
            <div style={{ flexBasis: "48%" }}>
              <h2 style={{ color: "#3498db" }}>Applications received:</h2>
              <ul>
                {applicationData.map((app, index) => (
                  <li
                    key={index}
                    style={{
                      marginBottom: "10px",
                      padding: "10px",
                      border: "1px solid #ddd",
                      borderRadius: "5px",
                      background: "#f9f9f9",
                      display: "flex",
                      justifyContent: "space-between",
                      alignItems: "center",
                    }}
                  >
                    <div>{app.email_id}</div>
                    <div>
                      <button
                        style={{
                          background: "#2ecc71",
                          color: "#fff",
                          padding: "5px 10px",
                          border: "none",
                          borderRadius: "5px",
                          cursor: "pointer",
                          marginRight: "5px",
                        }}
                        onClick={() =>
                          handleApproveapp(
                            app.id,
                            "application",
                            app.rollNumber
                          )
                        }
                      >
                        Approve
                      </button>
                      <button
                        style={{
                          background: "#e74c3c",
                          color: "#fff",
                          padding: "5px 10px",
                          border: "none",
                          borderRadius: "5px",
                          cursor: "pointer",
                        }}
                        onClick={() => handleDenyapp(app.id)}
                      >
                        Deny
                      </button>
                    </div>
                  </li>
                ))}
              </ul>
            </div>

            <div
              style={{
                borderLeft: "2px solid #000",
                height: "100%",
                margin: "0 10px",
              }}
            ></div>

            <div style={{ flexBasis: "48%" }}>
              <h2 style={{ color: "#3498db" }}>Pending Requests:</h2>
              {Object.entries(groupRequestsByRollNumber()).map(
                ([rollNumber, requests]) => (
                  <div
                    key={rollNumber}
                    style={{
                      marginBottom: "20px",
                    }}
                  >
                    <h3>{`Roll Number: ${rollNumber}`}</h3>
                    <ul>
                      {requests.map((request) => (
                        <li
                          key={request.id}
                          style={{
                            marginBottom: "10px",
                            padding: "10px",
                            border: "1px solid #ddd",
                            borderRadius: "5px",
                            background: "#f9f9f9",
                            display: "flex",
                            justifyContent: "space-between",
                            alignItems: "center",
                          }}
                        >
                          <div>{request.department_name}</div>
                          <div>
                            <button
                              style={{
                                background: "#2ecc71",
                                color: "#fff",
                                padding: "5px 10px",
                                border: "none",
                                borderRadius: "5px",
                                cursor: "pointer",
                                marginRight: "5px",
                              }}
                              onClick={() =>
                                handleApprove(
                                  request.id,
                                  "request",
                                  parseInt(rollNumber),
                                  request.department_name
                                )
                              }
                            >
                              Approve
                            </button>
                            <button
                              style={{
                                background: "#e74c3c",
                                color: "#fff",
                                padding: "5px 10px",
                                border: "none",
                                borderRadius: "5px",
                                cursor: "pointer",
                              }}
                              onClick={() =>
                                handleDeny(
                                  request.id,
                                  rollNumber,
                                  request.department_name
                                )
                              }
                            >
                              Deny
                            </button>
                          </div>
                        </li>
                      ))}
                    </ul>
                  </div>
                )
              )}
            </div>
          </div>
        </>
      )}
    </>
  );
};

export default Admin;
const AppContainer = styled.div`
  padding: 20px;
  background-image: url(${backgroundImage});
  background-size: cover;
  background-position: center;
  filter: brightness(70%);
  min-height: 100vh;
  position: relative;
`;
