import React, { useState } from "react";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Overlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.1);
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Form = styled.form`
  background-color: #fff;
  padding: 50px;
  border-radius: 8px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 10px;
  font-weight: bold;
`;

const Input = styled.input`
  width: 100%;
  padding: 8px;
  margin-bottom: 20px;
  border: 2px solid #3498db;
  borderradius: 50%;
`;

const Button = styled.button`
  background-color: #3498db;
  color: #fff;
  padding: 10px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s ease;

  &:hover {
    background-color: #2980b9;
  }
`;

const Login = () => {
  const [email, setEmail] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const adminLink = `admin.html?email=${email}`;
    window.location.href = adminLink;
  };

  return (
    <Overlay>
      <Container>
        <Form onSubmit={handleSubmit}>
          <h2>
            Login <span>Page</span>
          </h2>
          <Label>
            Roll Number:<span>&#9733;</span>
          </Label>
          <Input
            type="text"
            name="rollnumber"
            placeholder="Roll Number..."
            required
            pattern="[0-9]{8}"
            title="Please enter a valid roll number"
          />
          <Label>
            Password:<span>&#9733;</span>
          </Label>
          <Input
            type="password"
            name="password"
            placeholder="Password..."
            required
            pattern="^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{6,12}$"
            title="Password contains at least one uppercase letter, one lowercase letter, one digit and one special character. Password length should be between 6 to 12"
          />
          <Button type="submit">Login</Button>
        </Form>
        <br />
        <a href={`admin.html?email=${email}`}>
          If you don't have a Roll Number and Password? Click here to contact
          admin.
        </a>
      </Container>
    </Overlay>
  );
};

export default Login;
