    const rollNumberPattern = /^[0-9]{8}$/;
    const passwordPattern =
      /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{6,12}$/;
    if (!rollNumberPattern.test(rollNumber)) {
      setErrorMessage(
        <div>
          Please enter a valid roll number.
          <br />
          It should be of 8 digits.
          <br />
          It should not contain any special characters.
        </div>
      );

      return;
    }
    if (!passwordPattern.test(password)) {
      setErrorMessage(
        <div>
          Password must meet the specified criteria.
          <br />
          Password contains at least one uppercase letter,
          <br />
          one lowercase letter,
          <br />
          one digit, and one special character.
          <br />
          Password length should be between 6 to 12.
        </div>
      );

      return;
    }
    fetch("http://localhost:3001/api/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ rollNumber, password }),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.success === true) {
          if (data.isAdmin === true) {
            navigate("/admin");
          } else {
            navigate("/user-profile", {
              state: {
                Data: data.user,
                departmentDetails: data.departmentDetails,
              },
            });
          }
        } else {
          setErrorMessage(data.message);
        }
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  const handleRegistrationSubmit = () => {
    setErrorMessage("");
    if (
      !rollNumber ||
      !password ||
      !name ||
      !photoURL ||
      !schoolOfStudy ||
      !admissionYear ||
      !address ||
      !contactNumber ||
      !email
    ) {
      setErrorMessage("All fields are required.");
      return;
    }
    const rollNumberPattern = /^[0-9]{8}$/;
    if (!rollNumberPattern.test(rollNumber)) {
      setErrorMessage(
        <div>
          Please enter a valid roll number.
          <br />
          It should be of 8 digits.
          <br />
          It should not contain any special characters.
        </div>
      );
      return;
    }
    const passwordPattern =
      /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{6,12}$/;
    if (!passwordPattern.test(password)) {
      setErrorMessage(
        <div>
          Password must meet the specified criteria.
          <br />
          Password contains at least one uppercase letter,
          <br />
          one lowercase letter,
          <br />
          one digit, and one special character.
          <br />
          Password length should be between 6 to 12.
        </div>
      );
      return;
    }
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailPattern.test(email)) {
      setErrorMessage("Please enter a valid email address.");
      return;
    }
    const phoneNumberPattern = /^\d{10}$/;
    if (!phoneNumberPattern.test(contactNumber)) {
      setErrorMessage("Please enter a valid 10-digit phone number.");
      return;
    }
    const details = {
      rollNumber,
      password,
      name,
      photoURL,
      schoolOfStudy,
      admissionYear,
      address,
      contactNumber,
      email,
    };
    // console.log(details);
    fetch("http://localhost:3001/api/send-email", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(details),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.success === true) {
          alert(
            "Registered successfully admin have to Approval the Registration"
          );
          navigate("/");
        } else {
          setErrorMessage(data.message);
        }
      })
      .catch((error) => {
        console.error("Error:", error);
        setErrorMessage("An error occurred while processing your request.");
      });
  };

  const handleAdminContact = async () => {
    setShowLoginForm(false);
    setShowRegisterForm(true);
  };
  const handlelogin = async () => {
    setShowLoginForm(true);
    setShowRegisterForm(false);
  };
  return (
    <>
      <MarqueeText>
        <marquee>Welcome to No Due Management System(NDM).</marquee>
      </MarqueeText>
      <Overlay>
        <Container>
          {showLoginForm ? (
            <>
              <Form onSubmit={handleSubmit}>
                <h2>
                  Login <span>Page</span>
                </h2>
                <Label>
                  Roll Number:<span>&#9733;</span>
                </Label>
                <Input
                  type="text"
                  name="rollnumber"
                  placeholder="Roll Number..."
                  value={rollNumber}
                  onChange={(e) => setRollNumber(e.target.value)}
                />
                <Label>
                  Password:<span>&#9733;</span>
                </Label>
                <Input
                  type="password"
                  name="password"
                  placeholder="Password..."
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
                {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
                <Button type="button" onClick={handleSubmit}>
                  Login
                </Button>
              </Form>
              <br />
              <a href="#" onClick={handleAdminContact}>
                If you don't have a Roll Number and Password? Click here to
                contact admin.
              </a>
            </>
          ) : (
            <>
              {" "}
              <FormsContainer>
                <Form onSubmit={handleRegistrationSubmit}>
                  <h2>
                    Registration <span>Form</span>
                  </h2>
                  <Label>
                    Roll Number:<span>&#9733;</span>
                  </Label>
                  <Input
                    type="text"
                    name="rollnumber"
                    placeholder="Roll Number..."
                    value={rollNumber}
                    onChange={(e) => setRollNumber(e.target.value)}
                  />
                  <Label>
                    Password:<span>&#9733;</span>
                  </Label>
                  <Input
                    type="password"
                    name="password"
                    placeholder="Password..."
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                  <Label>
                    Name:<span>&#9733;</span>
                  </Label>
                  <Input
                    type="text"
                    name="name"
                    placeholder="Name..."
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                  />
                  <Label>
                    Photo URL:<span>&#9733;</span>
                  </Label>
                  <Input
                    type="text"
                    name="photoURL"
                    placeholder="Photo URL..."
                    value={photoURL}
                    onChange={(e) => setPhotoURL(e.target.value)}
                  />
                  <Label>
                    School of Study:<span>&#9733;</span>
                  </Label>
                  <Input
                    type="text"
                    name="schoolOfStudy"
                    placeholder="School of Study..."
                    value={schoolOfStudy}
                    onChange={(e) => setSchoolOfStudy(e.target.value)}
                  />
                </Form>
                <Form>
                  <Label>
                    Year of Admission:<span>&#9733;</span>
                  </Label>
                  <Input
                    type="text"
                    name="admissionYear"
                    placeholder="Year of Admission..."
                    value={admissionYear}
                    onChange={(e) => setAdmissionYear(e.target.value)}
                  />
                  <Label>
                    Address:<span>&#9733;</span>
                  </Label>
                  <Input
                    type="text"
                    name="address"
                    placeholder="Address..."
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                  />
                  <Label>
                    Contact Number:<span>&#9733;</span>
                  </Label>
                  <Input
                    type="text"
                    name="contactNumber"
                    placeholder="Contact Number..."
                    value={contactNumber}
                    onChange={(e) => setContactNumber(e.target.value)}
                  />
                  <Label>
                    Email:<span>&#9733;</span>
                  </Label>
                  <Input
                    type="email"
                    name="email"
                    placeholder="Email..."
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                  {errorMessage && (
                    <p style={{ color: "red" }}>{errorMessage}</p>
                  )}
                  <Button type="button" onClick={handleRegistrationSubmit}>
                    Register
                  </Button>
                </Form>
              </FormsContainer>
              <br />
              <a href="#" onClick={handlelogin}>
                If you already have a Roll Number and Password? Click here.
              </a>
            </>
          )}
        </Container>
      </Overlay>
    </>
  );
};

export default Login;
