import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const Admin = () => {
  const [applicationData, setApplicationData] = useState([]);
  const [pendingRequests, setPendingRequests] = useState([]);
  const navigate = useNavigate();
  const [errorMessage, setErrorMessage] = useState("");

  const handleLogout = () => {
    navigate("/");
  };

  useEffect(() => {
    fetchApplicationData();
    fetchPendingRequests();
  }, []);

  const fetchApplicationData = async () => {
    try {
      const response = await fetch(
        "http://localhost:3001/api/get-application-data"
      );
      const data = await response.json();

      if (data.success) {
        setApplicationData(data.applicationData);
      } else {
        console.error("Error fetching pending requests:", data.message);
      }
    } catch (error) {
      console.error("Error fetching application data:", error);
    }
  };

  const fetchPendingRequests = async () => {
    try {
      const response = await fetch(
        "http://localhost:3001/api/pending-requests"
      );
      const data = await response.json();

      if (data.success) {
        setPendingRequests(data.requests);
      } else {
        console.error("Error fetching pending requests:", data.message);
      }
    } catch (error) {
      console.error("Error fetching pending requests:", error);
    }
  };

  const handleApprove = async (id, type, applicationData) => {
    try {
      // console.log(id);
      const response = await fetch(
        "http://localhost:3001/api/insert-student-details",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ id }),
        }
      );

      const data = await response.json();

      if (data.success) {
        setErrorMessage("Student details inserted successfully.");
      } else {
        setErrorMessage(data.message.error.detail);
      }
    } catch (error) {
      console.error("Error approving student:", error);
    }
  };

  const groupRequestsByRollNumber = () => {
    const groupedRequests = {};
    pendingRequests.forEach((request) => {
      const { roll_number } = request;
      if (!groupedRequests[roll_number]) {
        groupedRequests[roll_number] = [];
      }
      groupedRequests[roll_number].push(request);
    });
    return groupedRequests;
  };

  return (
    <div>
      <button className="logoutButton" onClick={handleLogout}>
        Logout
      </button>
      <h2>Admin Page</h2>
      <div>
        <h2>Pending Requests:</h2>
        {Object.entries(groupRequestsByRollNumber()).map(
          ([rollNumber, requests]) => (
            <div key={rollNumber}>
              <h3>{`Roll Number: ${rollNumber}`}</h3>
              <ul>
                {requests.map((request) => (
                  <li key={request.id}>
                    {request.department_name}
                    <button
                      onClick={() =>
                        handleApprove(
                          request.id,
                          "request",
                          parseInt(rollNumber)
                        )
                      }
                    >
                      Approve
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          )
        )}
      </div>
      <div>
        <h2>Applications received:</h2>
        <ul>
          {applicationData.map((app, index) => (
            <li key={index}>
              {app.email_id}
              <button
                onClick={() =>
                  handleApprove(app.id, "application", app.rollNumber)
                }
              >
                Approve
              </button>
            </li>
          ))}
        </ul>
      </div>
      {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}
    </div>
  );
};

export default Admin;
