import React from "react";
import "./App.css";
import { Link } from "react-router-dom";
import Img from "./asset/img.png";

const Header = () => (
  <header className="App-header">
    <div className="container">
      <div className="header-content">
        <img
          style={{ borderRadius: "50%", border: "2px solid #3498db" }}
          src={Img}
          alt="No Due Management System Logo"
          width="150px"
          height="150px"
        />
        <div className="header-text">
          <h1>No Due Management System</h1>
          <p>
            Efficiently manage and track dues with our user-friendly system.
          </p>
        </div>
      </div>
    </div>
  </header>
);

const Features = () => (
  <section id="features" className="App-section">
    <div className="container">
      <img
        src={Img2}
        alt="No Due Management System Logo"
        width="150px"
        height="150px"
      />
      <h2>Key Features</h2>
      <ul>
        <li>Track dues and deadlines</li>
        <li>User-friendly interface</li>
        <li>Secure and reliable</li>
        <li>Easy integration</li>
      </ul>
    </div>
  </section>
);

const CTA = () => (
  <section id="cta" className="App-section App-cta">
    <div className="container">
      <h2>Get Started Today</h2>
      <p>Join thousands of users already managing their dues with NDM.</p>
      <Link to="/login" className="login-btn">
        Log In
      </Link>
    </div>
  </section>
);

const Footer = () => (
  <footer className="App-footer">
    <div className="container">
      <p>&copy; 2024 NDM by BMK</p>
    </div>
  </footer>
);

function App() {
  return (
    <div className="App">
      <Header />
      <Features />
      <CTA />
      <Footer />
    </div>
  );
}

export default App;
