import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import styled from "styled-components";

const UserProfile = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [message, setMessage] = useState("");
  const { Data, departmentDetails } = location.state;
  const [timerSeconds, setTimerSeconds] = useState(0);

  const handleLogout = () => {
    navigate("/");
  };
  const handleAskForApproval = async () => {
    try {
      const response = await fetch(
        "http://localhost:3001/api/request-approval",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            rollNumber: Data.roll_number,
            departmentDetails: departmentDetails,
          }),
        }
      );

      const data = await response.json();

      if (data.success) {
        alert("Request for approval sent successfully.");
        setMessage(data.timestamp);
      } else {
        alert("Error sending request for approval:", data.message);
      }
    } catch (error) {
      alert("Error sending request for approval:", error);
    }
  };

  const fetchTimestamp = async () => {
    try {
      const rollNumber = Data.roll_number;
      const response = await fetch("http://localhost:3001/api/get-timestamp", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ rollNumber }),
      });
      const data = await response.json();
      if (data.success) {
        const timestamp = new Date(data.timestamp).getTime() / 1000; // convert to seconds
        const currentTime = Math.floor(Date.now() / 1000); // current time in seconds
        const timeDifference = timestamp - currentTime;

        if (timeDifference > 0) {
          // set the timer only if the timestamp is in the future
          setTimerSeconds(timeDifference);
        }
      } else {
        console.error("Error fetching timestamp:", data.message);
      }
    } catch (error) {
      console.error("Error fetching timestamp:", error);
    }
  };

  useEffect(() => {
    fetchTimestamp();
  }, []);

  useEffect(() => {
    let timer;
    if (timerSeconds > 0) {
      timer = setTimeout(() => {}, timerSeconds * 1000);
    }
    return () => clearTimeout(timer);
  }, [timerSeconds]);
  return (
    <div>
      <MarqueeText>
        {/* eslint-disable-next-line jsx-a11y/no-distracting-elements */}
        <marquee>Welcome to No Due Management System(NDM).</marquee>
      </MarqueeText>
      <button className="logoutButton" onClick={handleLogout}>
        Logout
      </button>
      <h2
        style={{
          marginLeft: "1em",
        }}
      >
        Student Profile
      </h2>
      <div
        style={{
          display: "flex",
          alignItems: "flex-start",
          marginLeft: "1em",
        }}
      >
        <img
          src={Data.photo_url}
          height="150px"
          width="150px"
          alt="User"
          style={{ borderRadius: "50%", border: "2px solid #3498db" }}
        />
        <div
          style={{
            borderRight: "1px solid #3498db",
            paddingRight: "20px",
          }}
        >
          <div style={{ marginLeft: "20px" }}>
            <p>
              <strong>Roll Number:</strong> {Data.roll_number}
            </p>
            <p>
              <strong>Student Name:</strong> {Data.student_name}
            </p>
            <p>
              <strong>Email id:</strong> {Data.email_id}
            </p>
          </div>
        </div>
        <div style={{ marginLeft: "20px" }}>
          <p>
            <strong>Address:</strong> {Data.address}
          </p>
          <p>
            <strong>Contact Number:</strong> {Data.contact_number}
          </p>
          <p>
            <strong>School of Study:</strong> {Data.school_of_study}
          </p>
          <p>
            <strong>Year of Admission:</strong> {Data.year_of_admission}
          </p>
        </div>
      </div>

      <hr style={{ margin: "20px 0" }} />

      <h3
        style={{
          marginLeft: "1em",
        }}
      >
        No Due Details:
      </h3>
      {departmentDetails && departmentDetails.length > 0 ? (
        <div
          style={{
            display: "flex",
            textAlign: "center",
            alignItems: "center",
            marginLeft: "1em",
          }}
        >
          <div style={{ marginRight: "20px" }}>
            <p>
              <strong>Department:</strong>
            </p>
            <div style={{ marginRight: "20px" }}>
              <p>
                <strong>Status:</strong>
              </p>
            </div>
          </div>

          {departmentDetails.map((detail, index) => (
            <div key={index} style={{ marginRight: "20px" }}>
              <p>{detail.department_name}</p>
              <p>{detail.status}</p>
            </div>
          ))}
          <ButtonContainer>
            <button
              className="askApprovalButton"
              onClick={handleAskForApproval}
              disabled={timerSeconds > 0}
            >
              Ask for Approval
            </button>
          </ButtonContainer>
          {message}
        </div>
      ) : (
        <p>No department details available.May be You detained</p>
      )}
    </div>
  );
};

export default UserProfile;
const MarqueeText = styled.div`
  margin-top: 1em;
  width: 100%;
  overflow: hidden;
`;
const ButtonContainer = styled.div`
  margin-top: 20px;
  text-align: center;
`;
